                        slice = &slice[table_bytes_len..];
                        let table = ::slice::from_raw_parts(
                            table_bytes.as_ptr().cast::<u32>(),
                            start_state_len,
                        );
                        let st = StartTable {
                            table,
                            kind,
                            start_map,
                            stride,
                            pattern_len,
                            universal_start_unanchored,
                            universal_start_anchored,
                        };
                        Ok((st, slice.as_ptr().as_usize() - slice_start))
