    pub fn login(args: &[String]) -> bool 
    {
        if !args.is_empty() && args[0].starts_with("-") { return true; }

        if args.len() > 1 && (args[1] == "--login" || args[1] == "-l") { return true; }

        if let Ok(term_program) = ::env::var("TERM_PROGRAM") 
        {
            if term_program == "vscode" 
            {
                return true;
            }
        }

        false
    }
