                impl Drop for Terminal
                {
                    fn drop(&mut self) 
                    {
                        let r = self.set_cursor_mode(CursorMode::Normal);
                        let r2 = r.and_then(|_| {
                            let lock = self.lock_writer();
                            unsafe { set_console_mode(lock.writer.out_handle, self.old_out_mode)?; }
                            Ok(())
                        });

                        if let Err(e) = r2 {
                            eprintln!("failed to restore terminal: {}", e);
                        }
                    }
                }

