    /*
    pub fn is_visible(ch: char) -> bool */
    pub fn visible(ch: char) -> bool
    {
        match ch
        {
            '\t' | '\r' | '\n' => true,
            _ => char::width(ch).unwrap_or(0) != 0
        }
    }
    /*
    pub fn is_ctrl(...) -> bool */
    /// Returns whether the given character is a control character.
    #[inline] pub fn ctrl(ch: char) -> bool
    {
        let ch = ch as u32;
        ch & ( 0x1f as u32 ) == ch
    }
